package easy;

import java.util.Arrays;

/**
 * @author 喻浩
 * @create 2020-02-01-18:00
 */
public class find_the_difference_389 {
    /**
     * question url:https://leetcode-cn.com/problems/find-the-difference/
     * question context :
     * 给定两个字符串 s 和 t，它们只包含小写字母。
     *
     * 字符串 t 由字符串 s 随机重排，然后在随机位置添加一个字母。
     *
     * 请找出在 t 中被添加的字母。
     */
    static String s1 = "";
static String s2 ="";
    class Solution {
        public char findTheDifference(String s, String t) {
            char[] chars = t.toCharArray();
            for (int i = 0; i < chars.length && chars.length>1; i++) {
                int index = s.indexOf(chars[i]);
                if (index == -1){
                    return chars[i];
                }
                char[] array = s.toCharArray();
                String temp = "";
                temp = String.valueOf(Arrays.copyOfRange(array, 0, index));
                temp += String.valueOf(Arrays.copyOfRange(array, index+1, array.length));
                s = temp;
            }
            return chars[0];
        }
    }
    
    public static void main(String[] args) {
        find_the_difference_389 find_the_difference_389 = new find_the_difference_389();
        System.out.println(find_the_difference_389.new Solution().findTheDifference(s1,s2));
    }
}
